jQuery(document).ready(function () {
    var offset = 220;
    var duration = 500;
    jQuery(window).scroll(function () {
        if (jQuery(this).scrollTop() > offset) jQuery('.back-to-top').fadeIn(duration);
        else jQuery('.back-to-top').fadeOut(duration);
    });
    jQuery('.back-to-top').click(function (event) {
        event.preventDefault();
        jQuery('html, body').animate({
            scrollTop: 0
        }, duration);
        return false;
    });
});
jQuery("document").ready(function ($) {
    var nav = $('.header-container');
    $(window).scroll(function () {
        if ($(this).scrollTop() > 400) {
            nav.removeClass("container-fluid");
            nav.addClass("fixed");
        }
        else {
            nav.removeClass("fixed");
            nav.addClass("container-fluid");
         }
    });
});

function overlay() {
    el = document.getElementById("feedbackForm");
    el.style.visibility = (el.style.visibility == "visible") ? "hidden" : "visible";
}